import{_ as o}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as t,a as r}from"./app.38166bb6.js";var i="/DBC-Wiki/assets/t5tbjk5h0sq81.f1dcd3db.webp";const p={},n=r('<h1 id="proposal-26-incentive-program-for-cloud-platform-providers" tabindex="-1"><a class="header-anchor" href="#proposal-26-incentive-program-for-cloud-platform-providers" aria-hidden="true">#</a> Proposal 26: Incentive Program for Cloud Platform Providers</h1><div class="custom-container tip"><p class="custom-container-title">\u63D0\u793A</p><p>Reddit link: https://www.reddit.com/r/DBC_Council/comments/tt8kz4/proposal_26_incentive_program_for_cloud_platform/</p></div><p>**USING 300MIL OF THE 900MIL **</p><p>\u63D0\u6848\uFF1A\u4E91\u5E73\u53F0\u63D0\u4F9B\u5546\u6FC0\u52B1\u8BA1\u5212</p><p>NexuiCom \u5728\u4E0B\u9762\u63D0\u4F9B\u4E86\u4E00\u4E2A\u4E2D\u6587\u7FFB\u8BD1\uFF0C\u5C06 DBC \u793E\u533A\u805A\u96C6\u5728\u4E00\u8D77\u3002</p><p>Proponent: Fastwalker \u2013 NexuiCom</p><p><img src="'+i+`" alt="Bringing the World of Tomorrow to the People of Today"></p><p>Proposal: Incentives for Cloud Platform Providers</p><p>Proponent: Fastwalker \u2013 NexuiCom</p><p><strong>5FERzXKLRK3MDrgAKMq2WavcsckW4w3bXiWRQ6hEsyeJnc3L</strong></p><p>Date: 3/31/2022</p><p>Requested DOT: -</p><p>Short description: Incentives for Cloud Platform Providers, to help incentivize the increase of Client Based Accounts</p><pre><code>Context of the proposal:
</code></pre><p>The creation of incentives for the construction, marketing and acquisition of customers provided by the Cloud Platform Owner (CPO)</p><pre><code>Each rented machine should provide a 50% subsidy to the (CPO)

Each new user to the Cloud Platform should provide a subsidy of 50,000 DBC to the CPO

Each Cloud Platform subsidy should have a ceiling cap of 50,000,000 DBC

Up to a 6 Cloud Platform quota for a total of 300 Million DBC

Each CPO should apply individually for the Cloud incentive subsidy

This Proposal should be taken as an application to the Council for NexuiCom to be 1 of the 6 CPO to be offered such subsidy


Problem statement: NexuiCom is putting this proposal forward to help drive the construction and to establish Cloud Platforms across the world. Focus should be given not only to the building of the GPU mainframe but also the importance of creating interactive and well created on ramps, Cloud Platforms/Websites, for the customers to use the Blockchain technology created by DBChain.
</code></pre><p>NexuiCom, Bringing the World of Tomorrow to the People of Today</p><p>\u63D0\u6848\uFF1A\u5BF9\u4E91\u5E73\u53F0\u63D0\u4F9B\u5546\u7684\u6FC0\u52B1\u63AA\u65BD</p><p>\u652F\u6301\u8005\uFF1AFastwalker \u2013 NexuiCom</p><p><strong>5FERzXKLRK3MDrgAKMq2WavcsckW4w3bXiWRQ6hEsyeJnc3L</strong></p><p>\u65E5\u671F\uFF1A2022 \u5E74 3 \u6708 31 \u65E5</p><p>\u8BF7\u6C42\u7684 DOT\uFF1A-</p><p>\u7B80\u77ED\u63CF\u8FF0\uFF1A\u5BF9\u4E91\u5E73\u53F0\u63D0\u4F9B\u5546\u7684\u6FC0\u52B1\uFF0C\u4EE5\u5E2E\u52A9\u6FC0\u52B1\u57FA\u4E8E\u5BA2\u6237\u7684\u8D26\u6237\u7684\u589E\u52A0</p><p>\u4E00\u3001\u63D0\u6848\u5185\u5BB9\uFF1A</p><p>\u4E3A\u4E91\u5E73\u53F0\u6240\u6709\u8005\uFF08CPO\uFF09\u63D0\u4F9B\u7684\u5BA2\u6237\u7684\u5EFA\u8BBE\u3001\u8425\u9500\u548C\u83B7\u53D6\u521B\u9020\u6FC0\u52B1</p><p>1.\u6BCF\u53F0\u79DF\u7528\u673A\u5668\u5E94\u5411\uFF08CPO\uFF09\u63D0\u4F9B 50%\u7684\u8865\u8D34</p><ol start="2"><li><p>\u6BCF\u4F4D\u4E91\u5E73\u53F0\u65B0\u7528\u6237\u9700\u5411 CPO \u63D0\u4F9B 50,000 DBC \u8865\u8D34</p></li><li><p>\u6BCF\u4E2A\u4E91\u5E73\u53F0\u8865\u8D34\u4E0A\u9650\u4E3A 50,000,000 DBC</p></li><li><p>\u6700\u591A 6 \u4E2A\u4E91\u5E73\u53F0\u914D\u989D\u603B\u8BA1 3 \u4EBF DBC</p></li><li><p>\u6BCF\u4E2A CPO \u5E94\u5355\u72EC\u7533\u8BF7\u4E91\u6FC0\u52B1\u8865\u8D34</p></li><li><p>\u672C\u63D0\u6848\u5E94\u88AB\u89C6\u4E3A\u5411\u7406\u4E8B\u4F1A\u63D0\u51FA\u7533\u8BF7\uFF0C\u8981\u6C42 NexuiCom \u6210\u4E3A\u5C06\u63D0\u4F9B\u6B64\u7C7B\u8865\u8D34\u7684 6 \u540D CPO \u4E2D\u7684\u4E00\u540D</p><p>\u95EE\u9898\u9648\u8FF0\uFF1ANexuiCom \u63D0\u51FA\u8FD9\u4E2A\u5EFA\u8BAE\u662F\u4E3A\u4E86\u5E2E\u52A9\u63A8\u52A8\u5EFA\u8BBE\u548C\u5EFA\u7ACB\u5168\u7403\u4E91\u5E73\u53F0\u3002\u4E0D\u4EC5\u5E94\u8BE5\u5173\u6CE8 GPU \u5927\u578B\u673A\u7684\u6784\u5EFA\uFF0C\u8FD8\u5E94\u8BE5\u5173\u6CE8\u5728\u5761\u9053\u3001\u4E91\u5E73\u53F0/\u7F51\u7AD9\u4E0A\u521B\u5EFA\u4EA4\u4E92\u5F0F\u548C\u826F\u597D\u521B\u5EFA\u7684\u91CD\u8981\u6027\uFF0C\u4EE5\u4FBF\u5BA2\u6237\u4F7F\u7528 DBChain \u521B\u5EFA\u7684\u533A\u5757\u94FE\u6280\u672F\u3002</p></li></ol><p>NexuiCom\uFF0C\u4E3A\u4ECA\u5929\u7684\u4EBA\u4EEC\u5E26\u6765\u660E\u5929\u7684\u4E16\u754C</p>`,28),a=[n];function s(l,c){return e(),t("div",null,a)}var u=o(p,[["render",s],["__file","26.html.vue"]]);export{u as default};
