import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{o as a,c as r,a as e}from"./app.38166bb6.js";var s="/DBC-Wiki/assets/multisig1.27b4c66b.png",t="/DBC-Wiki/assets/multisig2.757020ac.png",l="/DBC-Wiki/assets/m3.18c96f99.png",p="/DBC-Wiki/assets/m4.465fb414.png",o="/DBC-Wiki/assets/m5.b8d8823c.png",c="/DBC-Wiki/assets/m6.cfb0f89e.png",n="/DBC-Wiki/assets/m7.8d037912.png";const d={},h=e('<h1 id="\u591A\u91CD\u7B7E\u540D\u8D26\u6237" tabindex="-1"><a class="header-anchor" href="#\u591A\u91CD\u7B7E\u540D\u8D26\u6237" aria-hidden="true">#</a> \u591A\u91CD\u7B7E\u540D\u8D26\u6237</h1><h2 id="\u4EC0\u4E48\u662F\u591A\u91CD\u7B7E\u540D\u8D26\u6237" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F\u591A\u91CD\u7B7E\u540D\u8D26\u6237" aria-hidden="true">#</a> \u4EC0\u4E48\u662F\u591A\u91CD\u7B7E\u540D\u8D26\u6237</h2><ul><li>\u53EF\u4EE5\u5728\u57FA\u4E8E Substrate \u7684\u94FE\u4E2D\u521B\u5EFA\u591A\u7B7E\u540D\u5E10\u6237\u3002\u4E00\u4E2A\u591A\u7B7E\u540D\u8D26\u6237\u7531\u4E00\u4E2A\u6216\u591A\u4E2A\u5730\u5740\u548C\u4E00\u4E2A\u9608\u503C\u7EC4\u6210\u3002\u9608\u503C\u5B9A\u4E49\u4E86\u9700\u8981\u591A\u5C11\u7B7E\u540D\u8005\uFF08\u53C2\u4E0E\u5730\u5740\uFF09\u540C\u610F\u63D0\u4EA4\u5916\u90E8\u4FE1\u606F\u624D\u80FD\u4F7F\u8C03\u7528\u6210\u529F\u3002</li><li>\u4F8B\u5982\uFF0CAlice\u3001Bob \u548C Charlie \u8BBE\u7F6E\u4E86\u4E00\u4E2A\u9608\u503C\u4E3A 2 \u7684\u591A\u91CD\u7B7E\u540D\u3002\u8FD9\u610F\u5473\u7740\u5373\u4F7F Charlie \u4E0D\u540C\u610F\uFF0CAlice \u548C Bob \u4E5F\u53EF\u4EE5\u6267\u884C\u4EFB\u4F55\u8C03\u7528\u3002\u540C\u6837\uFF0CCharlie \u548C Bob \u53EF\u4EE5\u5728\u6CA1\u6709 Alice \u7684\u60C5\u51B5\u4E0B\u6267\u884C\u4EFB\u4F55\u8C03\u7528\u3002\u9608\u503C\u901A\u5E38\u662F\u4E00\u4E2A\u5C0F\u4E8E\u6210\u5458\u603B\u6570\u7684\u6570\u5B57\uFF0C\u4F46\u4E5F\u53EF\u4EE5\u7B49\u4E8E\u5B83\uFF0C\u8FD9\u610F\u5473\u7740\u4ED6\u4EEC\u90FD\u5FC5\u987B\u8FBE\u6210\u4E00\u81F4\u3002</li></ul><h2 id="\u591A\u91CD\u7B7E\u540D\u8D26\u6237\u7684\u7528\u9014" tabindex="-1"><a class="header-anchor" href="#\u591A\u91CD\u7B7E\u540D\u8D26\u6237\u7684\u7528\u9014" aria-hidden="true">#</a> \u591A\u91CD\u7B7E\u540D\u8D26\u6237\u7684\u7528\u9014</h2><ul><li>\u4FDD\u62A4\u60A8\u7684\u8D22\u4EA7\u5B89\u5168\uFF1A\u4F7F\u7528\u989D\u5916\u7684\u7B7E\u7F72\u4EBA\u4F5C\u4E3A 2FA \u673A\u5236\u6765\u4FDD\u62A4\u60A8\u7684\u8D44\u91D1\u3002\u4E00\u4E2A\u7B7E\u540D\u8005\u53EF\u4EE5\u5728\u4E00\u53F0\u8BA1\u7B97\u673A\u4E0A\uFF0C\u53E6\u4E00\u4E2A\u53EF\u4EE5\u5728\u53E6\u4E00\u53F0\u8BA1\u7B97\u673A\u4E0A\uFF0C\u6216\u8005\u5728\u51B7\u5B58\u50A8\u4E2D\u3002\u8FD9\u4F1A\u51CF\u6162\u60A8\u4E0E\u94FE\u7684\u4EA4\u4E92\uFF0C\u4F46\u5B89\u5168\u6027\u9AD8\u51E0\u4E2A\u6570\u91CF\u7EA7\u3002</li><li>\u8463\u4E8B\u4F1A\u51B3\u7B56\uFF1A\u4F01\u4E1A\u548C\u57FA\u91D1\u4F1A\u7B49\u6CD5\u5F8B\u5B9E\u4F53\u4F7F\u7528\u591A\u91CD\u7B7E\u540D\u6765\u5171\u540C\u7BA1\u7406\u5B9E\u4F53\u7684\u8D22\u52A1\u3002</li><li>\u56E2\u4F53\u53C2\u4E0E\u6CBB\u7406\uFF1A\u591A\u91CD\u7B7E\u540D\u8D26\u6237\u53EF\u4EE5\u505A\u666E\u901A\u8D26\u6237\u53EF\u4EE5\u505A\u7684\u4E00\u5207\u3002\u591A\u91CD\u7B7E\u540D\u5E10\u6237\u53EF\u4EE5\u662F Kusama \u6CBB\u7406\u4E2D\u7684\u7406\u4E8B\u4F1A\u6210\u5458\uFF0C\u5176\u4E2D\u4E00\u7EC4\u793E\u533A\u6210\u5458\u53EF\u4EE5\u4F5C\u4E3A\u4E00\u4E2A\u5B9E\u4F53\u8FDB\u884C\u6295\u7968\u3002</li></ul><ul><li>\u6CE8\u610F\uFF1A\u591A\u91CD\u7B7E\u540D\u8D26\u6237<strong>\u521B\u5EFA\u540E\u4E0D\u53EF\u4FEE\u6539</strong>\u3002\u66F4\u6539\u6210\u5458\u96C6\u6216\u66F4\u6539\u9608\u503C\u662F\u4E0D\u53EF\u80FD\u7684\uFF0C\u800C\u662F\u9700\u8981\u89E3\u6563\u5F53\u524D\u7684\u591A\u91CD\u7B7E\u540D\u5E76\u521B\u5EFA\u4E00\u4E2A\u65B0\u7684\u591A\u91CD\u7B7E\u540D\u3002\u56E0\u6B64\uFF0C\u591A\u91CD\u7B7E\u540D\u5E10\u6237\u5730\u5740\u662F<strong>\u786E\u5B9A\u6027\u7684</strong>\uFF0C\u5373\u60A8\u59CB\u7EC8\u53EF\u4EE5\u901A\u8FC7\u77E5\u9053\u6210\u5458\u548C\u9608\u503C\u6765\u8BA1\u7B97\u591A\u91CD\u7B7E\u540D\u7684\u5730\u5740\uFF0C\u800C\u5E10\u6237\u5C1A\u4E0D\u5B58\u5728\u3002\u8FD9\u610F\u5473\u7740\u53EF\u4EE5\u5C06\u4EE4\u724C\u53D1\u9001\u5230\u5C1A\u4E0D\u5B58\u5728\u7684\u5730\u5740\uFF0C\u5982\u679C\u6307\u5B9A\u4E3A\u63A5\u6536\u8005\u7684\u5B9E\u4F53\u5728\u5339\u914D\u9608\u503C\u4E0B\u4EE5\u65B0\u7684\u591A\u91CD\u7B7E\u540D\u805A\u96C6\u5728\u4E00\u8D77\uFF0C\u4ED6\u4EEC\u5C06\u7ACB\u5373\u53EF\u4EE5\u8BBF\u95EE\u8FD9\u4E9B\u4EE4\u724C\u3002</li></ul><h2 id="\u5982\u4F55\u751F\u6210\u591A\u91CD\u7B7E\u540D\u8D26\u6237\u5730\u5740" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u751F\u6210\u591A\u91CD\u7B7E\u540D\u8D26\u6237\u5730\u5740" aria-hidden="true">#</a> \u5982\u4F55\u751F\u6210\u591A\u91CD\u7B7E\u540D\u8D26\u6237\u5730\u5740</h2><h3 id="\u4EE5\u6D4B\u8BD5\u7F51\u4E3A\u4F8B" tabindex="-1"><a class="header-anchor" href="#\u4EE5\u6D4B\u8BD5\u7F51\u4E3A\u4F8B" aria-hidden="true">#</a> \u4EE5\u6D4B\u8BD5\u7F51\u4E3A\u4F8B</h3><p><strong>\u8981\u521B\u5EFA\u591A\u91CD\u7B7E\u540D\u5730\u5740\u5E76\u4F7F\u7528\u5B83\u53D1\u9001\u4EA4\u6613\uFF0C\u60A8\u9700\u8981\u4EE5\u4E0B\u5185\u5BB9\uFF1A</strong></p><ul><li><p>\u591A\u91CD\u7B7E\u540D\u6210\u5458\u7684\u5730\u5740\u5217\u8868\u3002\u6211\u4EEC\u5C06\u4F7F\u7528 TEST\u3001ADN \u548C 175\u3002</p></li><li><p>DBC \u5B58\u5165\u591A\u91CD\u7B7E\u540D\u5730\u5740\u3002</p></li><li><p>\u591A\u7B7E\u8D26\u6237\u4EE5\u53CA\u6210\u5458\u8D26\u6237\u90FD\u9700\u8981\u6709 DBC\uFF0C\u6765\u652F\u4ED8\u4EA4\u6613\u8FC7\u7A0B\u4E2D\u4EA7\u751F\u7684\u53EF\u9000\u8FD8\u62BC\u91D1\u4EE5\u53CA\u624B\u7EED\u8D39\u3002</p><p>1.\u60A8\u5E94\u8BE5\u5DF2\u7ECF\u62E5\u6709\u81EA\u5DF1\u7684\u5E10\u6237\uFF0C\u91CC\u9762\u6709\u4E00\u4E9B\u786C\u5E01\u3002</p></li></ul><p><img src="'+s+'" alt="multisig1"></p><p>2.\u8981\u751F\u6210\u591A\u91CD\u7B7E\u540D\u5730\u5740\uFF0C\u6211\u4EEC\u9700\u8981<strong>\u5C06\u591A\u91CD\u7B7E\u540D\u6210\u5458\u5730\u5740\u6DFB\u52A0\u5230</strong>\u201C\u5E10\u6237 &gt; Address book\u201D\u4E0B\u7684\u901A\u8BAF\u5F55\u4E2D\u3002\u5BF9\u4E8E\u6BCF\u4E2A\u5730\u5740\uFF0C\u5355\u51FB\u53F3\u4E0A\u89D2\u7684\u201C\u6DFB\u52A0\u8054\u7CFB\u4EBA\u201D\u5E76\u63D0\u4F9B\u5730\u5740\u548C\u59D3\u540D</p><p><img src="'+t+'" alt="2"></p><p>3.**\u63A5\u4E0B\u6765\uFF0C\u6211\u4EEC\u9700\u8981\u521B\u5EFA\u65B0\u7684\u591A\u91CD\u7B7E\u540D\u5730\u5740\u3002**\u5BFC\u822A\u5230\u5E10\u6237\u9875\u9762\uFF08\u4ECE\u5DE5\u5177\u680F\uFF0C\u201C\u5E10\u6237&gt; \u5E10\u6237\u201D\uFF09\u5E76\u5355\u51FB\u201C+ \u591A\u91CD\u7B7E\u540D\u201D\u6309\u94AE\u3002\u6211\u4EEC\u5C06\u901A\u8BAF\u5F55\u5185\u4E24\u4E2A\u8D26\u6237\u548C\u81EA\u5DF1\u7684\u8D26\u6237\u4E09\u4E2A\u7EC4\u4E3A\u591A\u7B7E\u8D26\u6237\uFF0C\u9608\u503C\u4E3A\u201C2\u201D\u3002\uFF08\u9608\u503C\u8981\u5C0F\u4E8E\u7B49\u4E8E\u6210\u5458\u8D26\u6237\u6570\u91CF\u5EFA\u8BAE 2/3 3/5 \u7B49\u7EC4\u5408\uFF09\u5355\u51FB\u521B\u5EFA\uFF0C\u5373\u53EF\u53D1\u73B0\u8D26\u6237\u4E2D\u591A\u4E86\u4E00\u4E2A\u591A\u7B7E\u8D26\u6237\uFF0C\u5E76\u4E3A\u5176\u8F6C\u4E00\u4E9B\u5E01\u3002</p><p><img src="'+l+'" alt=""></p><p>4.**\u8981\u53D1\u9001\u4EA4\u6613\uFF0C\u6211\u4EEC\u9700\u8981\u5176\u4E2D\u4E00\u4E2A\u6210\u5458\u6765\u53D1\u8D77\u5B83\u3002\u4EE5 TEST \u6765\u53D1\u8D77\u4EA4\u6613\u6F14\u793A\u3002</p><p>\u786E\u4FDD TEST \u6709\u8DB3\u591F\u7684\u786C\u5E01\u6765\u652F\u4ED8\u591A\u91CD\u7B7E\u540D\u4EA4\u6613\u5B58\u6B3E\u548C\u4EA4\u6613\u8D39\u7528\u3002\u7136\u540E\uFF0C\u70B9\u51FB\u201C\u591A\u7B7E\u8D26\u6237\u201D\u4E0B\u7684\u201C\u53D1\u9001\u201D\uFF0C\u9009\u62E9\u76EE\u7684\u5730\u5740\uFF08\u6211\u4EEC\u5728\u672C\u5730\u751F\u6210\u5730\u5740\uFF09\u548C\u8F6C\u8D26\u91D1\u989D\uFF0C\u70B9\u51FB\u201C\u8FDB\u884C\u4EA4\u6613\u201D\u3002\u6279\u51C6\u4EA4\u6613\u4E2D\u4EA7\u751F\u7684\u591A\u91CD\u7B7E\u540D\u8C03\u7528\u6570\u636E\u8981\u590D\u5236\u51FA\u6765\uFF0C\u53D1\u9001\u7ED9\u591A\u7B7E\u8D26\u6237\u4E2D\u5176\u4ED6\u4EBA\uFF0C\u8FDB\u884C\u4EA4\u6613\u9A8C\u8BC1</p><p><img src="'+p+'" alt=""></p><p>5.\u60A8\u73B0\u5728\u5C06\u770B\u5230\u201C\u591A\u91CD\u7B7E\u540D\u6D4B\u8BD5\u5730\u5740\u201D\uFF08\u7EA2\u8272\u7684\u201C1\u201D\u56FE\u6807\uFF09\u7684\u5F85\u5904\u7406\u4EA4\u6613\uFF0C\u5982\u679C\u60A8\u5355\u51FB TEST \u4F59\u989D\u4E0B\u7684\u4E0B\u62C9\u83DC\u5355\uFF0C\u60A8\u5C06\u770B\u5230\u76F8\u5F53\u4E8E\u591A\u91CD\u7B7E\u540D\u5B58\u6B3E\u7684\u503C\u5DF2\u88AB\u201C\u4FDD\u7559\u201D\uFF0C\u5448\u73B0\u5728\u591A\u91CD\u7B7E\u540D\u4EA4\u6613\u5B8C\u6210\u4E4B\u524D\uFF0C\u8BE5\u503C\u4E0D\u53EF\u8F6C\u8BA9\u3002</p><p><img src="'+o+'" alt=""></p><p>6.**\u63A5\u4E0B\u6765\uFF0C\u6211\u4EEC\u9700\u8981\u7B2C\u4E8C\u4E2A\u7B7E\u540D\u3002**\u8BA9\u6211\u4EEC\u4ECE ADN \u90A3\u91CC\u5F97\u5230\u5B83\u3002\u5728 ADN \u7684\u6D4F\u89C8\u5668\u4E2D\uFF0C\u4ECE\u4E0A\u8FF0\u6B65\u9AA4\u91CD\u590D\u4EE5\u4E0B\u64CD\u4F5C\u3002</p><p>6.1. \u5C06 175\u3001TEST \u548C\u591A\u91CD\u7B7E\u540D\u4EA4\u6613\u76EE\u6807\u5730\u5740\u6DFB\u52A0\u5230 ADN \u7684\u5730\u5740\u7C3F\u3002 6.2. \u4F7F\u7528\u76F8\u540C\u7684\u53C2\u6570\uFF08ADN\u3001TEST \u548C 175 \u7684\u5730\u5740\uFF0C\u9608\u503C\u4E3A\u201C2\u201D\uFF09\u521B\u5EFA\u4E00\u4E2A\u65B0\u7684\u591A\u91CD\u7B7E\u540D\u5730\u5740\u3002</p><blockquote><p>\u6CE8\u610F\uFF1A\u7531\u4E8E\u591A\u91CD\u7B7E\u540D\u5730\u5740\u7684\u751F\u6210\u662F\u786E\u5B9A\u6027\u7684\uFF0C\u5982\u679C Bob\uFF08\u6216\u4EFB\u4F55\u5176\u4ED6\u6210\u5458\uFF09\u5728\u4ED6\u7684\u8BA1\u7B97\u673A\u4E0A\u4F7F\u7528 Alice\u3001Charlie \u548C\u4ED6\u7684\u5730\u5740\u751F\u6210\u4E00\u4E2A\u591A\u91CD\u7B7E\u540D\u5730\u5740\uFF0C\u9608\u503C\u4E3A\u201C2\u201D\uFF0C\u4ED6\u5C06\u751F\u6210<strong>\u76F8\u540C\u7684</strong>Alice \u5728\u8FD9\u91CC\u62E5\u6709\u7684\u591A\u91CD\u7B7E\u540D\u5730\u5740\u3002</p></blockquote><p>\u5982\u679C\u64CD\u4F5C\u6B63\u786E\uFF0C\u6211\u4EEC\u5E94\u8BE5\u4F1A\u770B\u5230 ADN \u7684\u6D4F\u89C8\u5668\u4E2D\u751F\u6210\u4E86<strong>\u76F8\u540C\u7684</strong>\u591A\u91CD\u7B7E\u540D\u5730\u5740\uFF0C\u5E76\u4E14\u8FD8\u663E\u793A\u4E86\u4E00\u4E2A\u5F85\u5904\u7406\u7684\u4EA4\u6613\u3002</p><p>\u63A5\u4E0B\u6765\uFF0C\u4E3A\u4E86\u83B7\u5F97 ADN \u7684\u7B7E\u540D\uFF0C\u4ED6\u9700\u8981\u901A\u8FC7\u63D0\u4F9B\u76F8\u540C\u7684\u76EE\u6807\u5730\u5740\u548C\u8F6C\u8D26\u91D1\u989D\uFF08\u4EE5\u53CA\u4EA4\u6613\u53C2\u6570\uFF09\u3001\u7B7E\u540D\u548C\u63D0\u4EA4\u6765\u5236\u4F5C\u4E0E TEST \u6240\u505A\u7684\u76F8\u540C\u7684\u591A\u91CD\u7B7E\u540D\u4EA4\u6613\u3002TEST \u901A\u8FC7\u4E0A\u4F20\u4EA4\u6613\u54C8\u5E0C\u548C\u54C8\u5E0C\u7684\u7B7E\u540D\u6765\u53D1\u8D77\u4EA4\u6613\u3002\u8FD9\u4E9B\u4EA4\u6613\u53C2\u6570\u5C06\u5141\u8BB8 AND \u751F\u6210\u5E76\u7B7E\u7F72 TEST \u4E4B\u524D\u7B7E\u7F72\u7684\u76F8\u540C\u4EA4\u6613\uFF08\u76F8\u540C\u7684\u54C8\u5E0C\uFF09\u3002</p><blockquote><p>\u5373\uFF1A\u5982\u679C\u4E0D\u4F7F\u7528\u591A\u91CD\u7B7E\u540D\u8C03\u7528\u6570\u636E\u6765\u9A8C\u8BC1\u901A\u8FC7\u4EA4\u6613\u7684\u3002\u53EF\u4EE5\u4F7F\u7528\u53E6\u5916\u4E00\u4E2A\u8D26\u6237\u4F5C\u4E3A\u4EA4\u6613\u53D1\u8D77\u4EBA\uFF0C\u5411\u540C\u4E00\u4E2A\u6536\u6B3E\u8D26\u6237\u53D1\u9001\u540C\u6837\u591A\u7684\u5E01\uFF0C\u4E5F\u89C6\u4E3A\u901A\u8FC7\u6B64\u6B21\u4EA4\u6613\uFF0C\u4F60\u53EF\u4EE5\u770B\u5230\u4EE5\u4E0B\u4E24\u79CD\u64CD\u4F5C\u65B9\u5F0F</p></blockquote><p><img src="'+c+'" alt=""></p><ul><li>\u8FD9\u4E00\u4E2A\u662F\u9700\u8981\u586B\u5199\u6211\u4EEC\u7B2C\u4E00\u4F4D\u53D1\u8D77\u4EA4\u6613\u65F6\u4EA7\u751F\u7684\u591A\u91CD\u7B7E\u540D\u8C03\u7528\u6570\u636E\uFF0C\u5E76\u70B9\u51FB\u7B7E\u540D\u63D0\u4EA4\u901A\u8FC7</li></ul><p><img src="'+n+'" alt=""></p><ul><li>\u8FD9\u4E00\u4E2A\u662F\u6211\u4EEC\u6CA1\u6709\u6536\u5230\u591A\u91CD\u7B7E\u540D\u8C03\u7528\u6570\u636E\uFF0C\u4E09\u4E2A\u4EBA\u90FD\u77E5\u9053\u7ED9\u76EE\u6807\u8D26\u6237\u8F6C\u51C6\u786E\u6570\u989D DBC \u65F6\uFF0C\u53EF\u4EE5\u91C7\u7528\u70B9\u51FB<code>\u53D1\u9001</code>----&gt;<code>\u8F93\u5165\u4E0E\u7B2C\u4E00\u4E2A\u53D1\u8D77\u4EBA\u540C\u7B49\u6570\u91CF\u7684DBC</code>\u5E76\u7B7E\u540D\u63D0\u4EA4\u5373\u53EF\u901A\u8FC7\u6B64\u6B21\u4EA4\u6613\u3002</li></ul>',30),g=[h];function m(u,_){return a(),r("div",null,g)}var T=i(d,[["render",m],["__file","multi-sig.html.vue"]]);export{T as default};
